    @Output() imageDataChange = new EventEmitter();
    @Input()
    get imageData() {
        return this.imageDataValue;
    }

    set imageData(val) {
        this.imageDataValue = val;
        if (val || val === '' || this.isDataURL(val)) {
            this.saveCavas(val);
        }
        this.imageDataChange.emit(this.imageDataValue);
    }

